C<pre><code>typedef struct BinNode *Position;
typedef struct Collection *BinQueue;
typedef struct BinNode *BinTree;  /* missing from p.176 */

struct BinNode 
{ 
    ElementType     Element;
    Position        LeftChild;
    Position        NextSibling;
} ;

struct Collection 
{ 
    int         CurrentSize;  /* total number of nodes */
    BinTree TheTrees[ MaxTrees ];
} ;

BinTree CombineTrees( BinTree T1, BinTree T2 )
{  /* merge equal-sized T1 and T2 */
    if ( T1-&gt;Element &gt; T2-&gt;Element )
        /* attach the larger one to the smaller one */
        return CombineTrees( T2, T1 );
    /* insert T2 to the front of the children list of T1 */
    T2-&gt;NextSibling = T1-&gt;LeftChild;
    T1-&gt;LeftChild = T2;
    return T1;
}
//Merge
BinQueue  Merge( BinQueue H1, BinQueue H2 )
{   BinTree T1, T2, Carry = NULL;   
    int i, j;
    if ( H1-&gt;CurrentSize + H2-&gt; CurrentSize &gt; Capacity )  ErrorMessage();
    H1-&gt;CurrentSize += H2-&gt; CurrentSize;
    for ( i=0, j=1; j&lt;= H1-&gt;CurrentSize; i++, j*=2 ) {
        T1 = H1-&gt;TheTrees[i]; T2 = H2-&gt;TheTrees[i]; /*current trees */
        switch( 4*!!Carry + 2*!!T2 + !!T1 ) { 
        case 0: /* 000 */
        case 1: /* 001 */  break;   
        case 2: /* 010 */  H1-&gt;TheTrees[i] = T2; H2-&gt;TheTrees[i] = NULL; break;
        case 4: /* 100 */  H1-&gt;TheTrees[i] = Carry; Carry = NULL; break;
        case 3: /* 011 */  Carry = CombineTrees( T1, T2 );
                        H1-&gt;TheTrees[i] = H2-&gt;TheTrees[i] = NULL; break;
        case 5: /* 101 */  Carry = CombineTrees( T1, Carry );
                        H1-&gt;TheTrees[i] = NULL; break;
        case 6: /* 110 */  Carry = CombineTrees( T2, Carry );
                        H2-&gt;TheTrees[i] = NULL; break;
        case 7: /* 111 */  H1-&gt;TheTrees[i] = Carry; 
                        Carry = CombineTrees( T1, T2 ); 
                        H2-&gt;TheTrees[i] = NULL; break;
        } /* end switch */
    } /* end for-loop */
    return H1;
}
//DeleteMin
ElementType  DeleteMin( BinQueue H )
{   BinQueue DeletedQueue; 
    Position DeletedTree, OldRoot;
    ElementType MinItem = Infinity;  /* the minimum item to be returned */  
    int i, j, MinTree; /* MinTree is the index of the tree with the minimum item */

    if ( IsEmpty( H ) )  {  PrintErrorMessage();  return –Infinity; }

    for ( i = 0; i &lt; MaxTrees; i++) {  /* Step 1: find the minimum item */
        if( H-&gt;TheTrees[i] &amp;&amp; H-&gt;TheTrees[i]-&gt;Element &lt; MinItem ) { 
        MinItem = H-&gt;TheTrees[i]-&gt;Element;  MinTree = i;    } /* end if */
    } /* end for-i-loop */
    DeletedTree = H-&gt;TheTrees[ MinTree ];  
    H-&gt;TheTrees[ MinTree ] = NULL;   /* Step 2: remove the MinTree from H =&gt; H’ */ 
    OldRoot = DeletedTree;   /* Step 3.1: remove the root */ 
    DeletedTree = DeletedTree-&gt;LeftChild;   free(OldRoot);
    DeletedQueue = Initialize();   /* Step 3.2: create H” */ 
    DeletedQueue-&gt;CurrentSize = ( 1&lt;&lt;MinTree ) – 1;  /* 2MinTree – 1 */
    for ( j = MinTree – 1; j &gt;= 0; j – – ) {  
        DeletedQueue-&gt;TheTrees[j] = DeletedTree;
        DeletedTree = DeletedTree-&gt;NextSibling;
        DeletedQueue-&gt;TheTrees[j]-&gt;NextSibling = NULL;
    } /* end for-j-loop */
    H-&gt;CurrentSize  – = DeletedQueue-&gt;CurrentSize + 1;
    H = Merge( H, DeletedQueue ); /* Step 4: merge H’ and H” */ 
    return MinItem;
}
</code></pre>

C<pre><code>bool Backtracking ( int i )
{   Found = false;
    if ( i &gt; N )
        return true; /* solved with (x1, …, xN) */
    for ( each xi in Si ) { 
        /* check if satisfies the restriction R */
        OK = Check((x1, …, xi) , R ); /* pruning */
        if ( OK ) {     
            Count xi in;
            Found = Backtracking( i+1 );
            if ( !Found )
                Undo( i ); /* recover to (x1, …, xi-1) */
        }
        if ( Found ) break; 
    }
    return Found;
}
</code></pre>

C<pre><code>void Huffman ( PriorityQueue  heap[ ],  int  C )
{   consider the C characters as C single node binary trees,
     and initialize them into a min heap;
     for ( i = 1; i &lt; C; i++ ) { 
        create a new node;
        /* be greedy here */
        delete root from min heap and attach it to left_child of node;
        delete root from min heap and attach it to right_child of node;
        weight of node = sum of weights of its children;
        /* weight of a tree = sum of the frequencies of its leaves */
        insert node into min heap;
   }
}
</code></pre> \[ T=O(C\log C) \]

C<pre><code>SolutionType Metropolis()
{   Define constants k and T;
    Start from a feasible solution S in FS ;
    MinCost = cost(S);
    while (1) {
        S’ = Randomly chosen from N(S); //Adding is allowed
        CurrentCost = cost(S’);
        if ( CurrentCost &lt; MinCost ) {
            MinCost = CurrentCost;    S = S’;
        }
        else {
            With a probability e^{-d cost/(KT)}  , let S = S’;
            else  break;
        }
    }
    return S;
}
</code></pre>

C<pre><code>ConfigType State_flipping()
{
    Start from an arbitrary configuration S;
    while ( ! IsStable(S) ) {
        u = GetUnsatisfied(S);
        su = - su;
    }
    return S;
}
