C<pre><code>int OnlineHiring ( EventType C[ ], int N, int k )
{
    int Best = N;
    int BestQ = -  ;
    for ( i=1; i&lt;=k; i++ ) {
        Qi = interview( i );
        if ( Qi &gt; BestQ )   BestQ = Qi;
    }
    for ( i=k+1; i&lt;=N; i++ ) {
        Qi = interview( i );
        if ( Qi &gt; BestQ ) {
            Best = i;
            break;
        }
    }
    return Best;
}
